(()=>{"use strict";document.addEventListener("DOMContentLoaded",(function(){var e=document.getElementById("language"),n=document.getElementById("apikey"),t=document.getElementById("save");chrome.storage.sync.get("language",(function(n){e.value=n.language||"en"})),chrome.storage.sync.get("apikey",(function(e){n.value=e.apikey||""})),t.addEventListener("click",(function(){chrome.storage.sync.set({language:e.value},(function(){alert("Language saved!")})),chrome.storage.sync.set({apikey:n.value},(function(){alert("api saved!")}))}))}))})();